import React, { useState } from 'react';
import { Button } from 'primereact/button';

export function AddBook(props) {
    const [bookData, setBookData] = useState({
        title: '',
        author: '',
        genre: '',
        year: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setBookData({
            ...bookData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch("http://localhost:8080/book", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(bookData)
        })
            .then(response => response.json())
            .then(data => {
                console.log("Libro agregado exitosamente:", data);
                props.onSave();
            })
            .catch(error => {
                console.error("Error al agregar libro:", error);
            });
    };

    const handleCancel = () => {
        props.onCancel();
    };

    return (
        <div className="add-book-form">
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Título:</label>
                    <input
                        type="text"
                        name="title"
                        value={bookData.title}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Autor:</label>
                    <input
                        type="text"
                        name="author"
                        value={bookData.author}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Género:</label>
                    <input
                        type="text"
                        name="genre"
                        value={bookData.genre}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Año:</label>
                    <input
                        type="text"
                        name="year"
                        value={bookData.year}
                        onChange={handleChange}
                    />
                </div>
                <div className="button-group" style={{ textAlign: 'center', marginBottom: '20px' }}>
                    <Button label="Guardar" icon="pi pi-save" onClick={handleSubmit} className="p-button-secondary" raised rounded />
                </div>
            </form>
        </div>
    );
}
