import React, { useEffect, useState } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import 'primereact/resources/themes/saga-orange/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';
import { Dialog } from 'primereact/dialog';
import { AddAuthor } from './AddAuthor';




export function Author() {
    const [authors, setAuthors] = useState([]);
    const [displayDialog, setDisplayDialog] = useState(false);
    const [author, setAuthor] = useState(null);
    const [displayEditDialog, setDisplayEditDialog] = useState(false);
    const [authorToEdit, setAuthorToEdit] = useState(null);

    useEffect(() => {
        fetchData();
    }, []); 

    const fetchData = () => {
        fetch("http://localhost:8080/author", {
            method: "GET",
        })
        .then((res) => res.json())
        .then((data) => {
            setAuthors(data); 
        });
    };

    const saveAuthor = () => {
        setDisplayDialog(false);
        fetchData();
    };

    const editAuthor = (author) => {
        setAuthorToEdit({...author}); // Clona el autor seleccionado en el estado
        setDisplayEditDialog(true); // Muestra la ventana modal de edición
    };

    const deleteAuthor = (author) => {
        fetch(`http://localhost:8080/author/${author.id}`, {
            method: "DELETE",
        })
        .then(() => {
            console.log("Autor eliminado exitosamente");
            fetchData(); // Actualiza la lista después de eliminar
        })
        .catch(error => {
            console.error("Error al eliminar autor:", error);
        });
    };


    return (
        <div>
            <h1 style={{textAlign: 'center'}}>Lista de Autores</h1>
            <Button label="Nuevo Autor" icon="pi pi-user-plus" onClick={() => setDisplayDialog(true)} className="p-button-success" text raised/>

            <DataTable value={authors} stripedRows tableStyle={{ minWidth: '10rem' }}>
                <Column field="authorFirstName" header="Nombre" />
                <Column field="authorLastName" header="Apellido" />
                <Column field="nationality" header="Nacionalidad" />
                <Column body={(rowData) => (
                    <div>
                        <Button icon="pi pi-pencil" onClick={() => editAuthor(rowData)} className="p-button-secondary" text raised/>
                        <Button icon="pi pi-trash" onClick={() => deleteAuthor(rowData)} className="p-button-danger" text raised/>
                    </div>
                )} />
            </DataTable>

            <Dialog header="Agregar Nuevo Autor" headerClassName="custom-dialog-header" visible={displayDialog} style={{ width: '45vw' }} onHide={() => setDisplayDialog(false)}>
                <AddAuthor onSave={saveAuthor} onCancel={() => setDisplayDialog(false)} />
            </Dialog>
        </div>
    );
}
