import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import './App.css';
import "primereact/resources/themes/saga-orange/theme.css";
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Author } from './Components/Authors/Author';
import { Book } from './Components/Books/Book';
import { Student } from './Components/Students/Student';
import { Loan } from './Components/Loans/Loan'; 
import HomePage from './Components/HomePage';

ReactDOM.render(
  <Router>
    <div>
      <h1 style={{ textAlign: 'center' }}>Biblioteca Alejandro Segovia</h1>
      <ul className="navbar-nav">
            <li className="nav-item">
              <a className="nav-link" href="#home">Home</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="http://localhost:3000/author">Autores</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="http://localhost:3000/book">Libros</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="http://localhost:3000/loan">Préstamos</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="http://localhost:3000/student">Estudiantes</a>
            </li>
            {/* Agrega más elementos de la barra de navegación aquí */}
          </ul>
    </div>
    <Routes>
      <Route path="/" element={<HomePage />} /> {/* Ruta para la página de inicio */}
      <Route path="/author" element={<Author />} />
      <Route path="/book" element={<Book />} />
      <Route path="/student" element={<Student />} />
      <Route path="/loan" element={<Loan />} />
    </Routes>
  </Router>,
  document.getElementById('root')
);
