import { useEffect, useState } from 'react';
import { SideBarAdmin } from './SideBarAdmin';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';


export function ReceiptTable(){
    const [visible, setVisible] = useState(false);
    const [receipt,setReceipt]=useState();
    const [pageNumber, setPageNumber] = useState(0); 
    const [pageSize, setPageSize] = useState(15); 

    useEffect(()=>{
        fetch(`http://localhost:8080/receipt?pageNumber=${pageNumber}`,{
            method:"GET"
        })
        .then((res=>res.json()))
        .then((data)=>{

            const {content}=data


            setReceipt(content)





        })


        },[])
        const formatDate = (dateString) => {
            const options = { day: '2-digit', month: '2-digit', year: '2-digit' };
            return new Date(dateString).toLocaleDateString('en-GB', options);
        };

    return (

    <>
    <SideBarAdmin visible={visible} onHide={() => setVisible(false)}></SideBarAdmin>
    <DataTable value={receipt} 
            paginator 
            rows={5} 
            first={pageNumber}
            rowsPerPageOptions={[5, 10, 25, 50]} 
            tableStyle={{ minWidth: '50rem' }}
            className='card-table'>
            <Column field="id" header="Receipt Id" />
            <Column field="user.firstName" header="First Name"/>
            <Column field="user.lastName" header=" Last Name"/>
            <Column 
                    field="receiptDate" 
                    header="Date of receipt"
                    body={(receipt) => formatDate(receipt.receiptDate)} // Aplica el formato a la fecha
            />



            </DataTable>

    </>)

}
