import React, { useState } from 'react';

export function AddAuthor(props) {
    const [authorData, setAuthorData] = useState({
        firstName: '',
        lastName: '',
        nationality: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAuthorData({
            ...authorData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch("http://localhost:8080/author", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(authorData)
        })
        .then(response => response.json())
        .then(data => {
            console.log("Autor agregado exitosamente:", data);
            props.onSave();
        })
        .catch(error => {
            console.error("Error al agregar autor:", error);
        });
    };

    const handleCancel = () => {
        props.onCancel();
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <div className="card">
                        <div className="card-body">
                            <h2 className="card-title text-center mb-4">Agregar Autor</h2>
                            <form onSubmit={handleSubmit}>
                                <div className="mb-3">
                                    <label className="form-label">Nombre:</label>
                                    <input
                                        type="text"
                                        name="firstName"
                                        value={authorData.firstName}
                                        onChange={handleChange}
                                        className="form-control"
                                        required
                                    />
                                </div>
                                <div className="mb-3">
                                    <label className="form-label">Apellido:</label>
                                    <input
                                        type="text"
                                        name="lastName"
                                        value={authorData.lastName}
                                        onChange={handleChange}
                                        className="form-control"
                                        required
                                    />
                                </div>
                                <div className="mb-3">
                                    <label className="form-label">Nacionalidad:</label>
                                    <input
                                        type="text"
                                        name="nationality"
                                        value={authorData.nationality}
                                        onChange={handleChange}
                                        className="form-control"
                                        required
                                    />
                                </div>
                                <div className="d-grid gap-2">
                                    <button type="submit" className="btn btn-primary">Guardar</button>
                                    <button type="button" onClick={handleCancel} className="btn btn-secondary">Cancelar</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
