import React, { useState } from 'react';

export function AddAuthor(props) {
    const [authorData, setAuthorData] = useState({
        firstName: '',
        lastName: '',
        nationality: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAuthorData({
            ...authorData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch("http://localhost:8080/author", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(authorData)
        })
        .then(response => response.json())
        .then(data => {
            console.log("Autor agregado exitosamente:", data);
            props.onSave();
        })
        .catch(error => {
            console.error("Error al agregar autor:", error);
        });
    };

    const handleCancel = () => {
        props.onCancel();
    };

    return (
        <div className="user-view _add-view">
            <h1>Informaci√≥n del Autor</h1>
            <div className="box">
                <form onSubmit={handleSubmit}>
                    <div className="row">
                        <div className="col-sm-12 col-md-6">
                            <p>
                                <span>Nombre:</span>
                                <input
                                    type="text"
                                    name="authorFirstName"
                                    value={authorData.authorFirstName}
                                    onChange={handleChange}
                                    className="form-control"
                                />
                            </p>
                        </div>
                        <div className="col-sm-12 col-md-6">
                            <p>
                                <span>Apellido:</span>
                                <input
                                    type="text"
                                    name="authorLastName"
                                    value={authorData.authorLastName}
                                    onChange={handleChange}
                                    className="form-control"
                                />
                            </p>
                        </div>
                        <div className="col-sm-12 col-md-6">
                            <p>
                                <span>Nacionalidad:</span>
                                <input
                                    type="text"
                                    name="nationality"
                                    value={authorData.nationality}
                                    onChange={handleChange}
                                    className="form-control"
                                />
                            </p>
                        </div>
                    </div>
                    <div className="button-group">
                        <button type="submit" className="btn btn-success">Guardar</button>
                        <button type="button" onClick={handleCancel} className="btn btn-secondary">Cancelar</button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default AddAuthor;
