import React, { useState, useEffect } from 'react';
import { Button } from 'primereact/button';

export function EditStudent(props) {
    const [studentData, setStudentData] = useState({
        firstName: '',
        lastName: '',
        studentId: ''
    });

    useEffect(() => {
        // Lógica para obtener los datos del estudiante desde el servidor
        fetch(`http://localhost:8080/student/${props.match.params.id}`)
            .then(response => response.json())
            .then(data => {
                setStudentData({
                    firstName: data.firstName,
                    lastName: data.lastName,
                    studentId: data.studentId
                });
            })
            .catch(error => console.error('Error al obtener los datos del estudiante:', error));
    }, [props.match.params.id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setStudentData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch(`http://localhost:8080/student/${props.match.params.id}`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(studentData)
        })
            .then(response => {
                if (response.ok) {
                    console.log("Estudiante actualizado exitosamente");
                    props.history.push('/');
                } else {
                    throw new Error('Error al actualizar estudiante');
                }
            })
            .catch(error => {
                console.error("Error al actualizar estudiante:", error);
            });
    };

    return (
        <div className="edit-student-form">
            <h2>Editar Estudiante</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Nombre:</label>
                    <input
                        type="text"
                        name="firstName"
                        value={studentData.firstName}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Apellido:</label>
                    <input
                        type="text"
                        name="lastName"
                        value={studentData.lastName}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>ID del Estudiante:</label>
                    <input
                        type="text"
                        name="studentId"
                        value={studentData.studentId}
                        onChange={handleChange}
                    />
                </div>
                <div className="button-group">
                    <Button label="Guardar" type="submit" className="p-button-secondary" />
                </div>
            </form>
        </div>
    );
}
