import { useState,useEffect } from 'react';
import './App.css';
import { Dropdown } from 'primereact/dropdown';

import { InputTextarea } from 'primereact/inputtextarea';
import { Button } from 'primereact/button';


function App() {

  const [userOptions, setUserOptions] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [value, setValue] = useState('');




  useEffect(() => {
    fetch("http://localhost:8080/user", {
      method: "GET",
    })
      .then((res) => res.json())
      .then((data) => {
        const firstNames=data.map(user=>({name:user.firstName, code: user.id}))
        setUserOptions(firstNames);

      });

  }, []);


  return (
    <div className='container'>
      <h3>Report your trumble</h3>
      <div className='form-container'>
        <div className='form'>
          <h4 className='form-h4'>Please choose your name</h4>
          <Dropdown
            value={selectedUser}
            onChange={(e) => setSelectedUser(e.value)}
            options={userOptions}
            optionLabel="name"
            placeholder="Select a User"
            className="w-full md:w-30rem"
          />
          <div className="description-container">
            <h4 className='form-h4'>Description</h4>
            <InputTextarea value={value} onChange={(e) => setValue(e.target.value)} rows={5} cols={30} />
          </div>
          <Button label="Send" type="submit" icon="pi pi-check" className="mt-4 custom-button" />
        </div>
      </div>
    </div>
  );
}

export default App;
