import React, { useEffect, useState } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import 'primereact/resources/themes/saga-orange/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';




export function Author() {
    const [authors, setAuthors] = useState([]);
    const [displayDialog, setDisplayDialog] = useState(false);
    useEffect(() => {
        fetchData();
    }, []); 

    const fetchData = () => {
        fetch("http://localhost:8080/author", {
            method: "GET",
        })
        .then((res) => res.json())
        .then((data) => {
            setAuthors(data); // Guarda los autores en el estado
        });
    };

    const saveAuthor = () => {
        fetch("http://localhost:8080/author", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(author) // Utiliza el estado 'author' para enviar el nuevo autor al servidor
        })
        .then(() => {
            // Después de guardar, actualizamos la lista de autores
            fetchData();
            // Finalmente, cerramos el diálogo
            setDisplayDialog(false);
        })
        .catch(error => console.error('Error al guardar el autor:', error));
    };
    

    const editAuthor = (author) => {
        // Lógica para editar un autor
        setAuthor(author);
        setDisplayDialog(true);
    };

    const deleteAuthor = (author) => {
        // Lógica para eliminar un autor
        fetchData(); // Actualiza la lista después de eliminar
    };

    return (
        <div>
            <div >
                <h1 style={{textAlign: 'center'}} >Lista de Autores</h1>
            </div>
            
            <Button label="Nuevo Autor" icon="pi pi-user-plus" onClick={() => setDisplayDialog(true)} className="p-button-success"  rounded/>
            <div className='authors-list'>
            <DataTable value={authors} stripedRows tableStyle={{ minWidth: '50rem' }}>
                <Column field="authorFirstName" header="Nombre" />
                <Column field="authorLastName" header="Apellido" />
                <Column field="nationality" header="Nacionalidad" />
                <Column body={(rowData) => (
                    <div>
                        <Button icon='pi pi-pencil' onClick={() => editAuthor(rowData)} className="p-button-secondary" rounded/>
                        <Button icon="pi pi-trash" onClick={() => deleteAuthor(rowData)} className="p-button-danger" rounded/>
                    </div>
                )} />
                <Column header="nationality" header="Nacionalidad" />
            </DataTable>
            </div>
            
        </div>
    );
}
