import React, { useState, useEffect } from 'react';
import { Button } from 'primereact/button';
import '../../App.css';

export function EditAuthor(props) {
    const [authorData, setAuthorData] = useState({
        authorFirstName: '',
        authorLastName: '',
        nationality: ''
    });

    useEffect(() => {
        if (props.author) {
            setAuthorData({
                authorFirstName: props.author.authorFirstName,
                authorLastName: props.author.authorLastName,
                nationality: props.author.nationality
            });
        }
    }, [props.author]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAuthorData({
            ...authorData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        props.onUpdate(authorData);
    };

    const handleCancel = () => {
        props.onCancel();
    };

    return (
        <div className="add-author-form">
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Nombre:</label>
                    <input
                        type="text"
                        name="authorFirstName"
                        value={authorData.authorFirstName}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Apellido:</label>
                    <input
                        type="text"
                        name="authorLastName"
                        value={authorData.authorLastName}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Nacionalidad:</label>
                    <input
                        type="text"
                        name="nationality"
                        value={authorData.nationality}
                        onChange={handleChange}
                    />
                </div>
                <div className="button-group">
                    <Button label="Guardar" icon="pi pi-save" type="submit" className="p-button-secondary" raised />
                    <Button label="Cancelar" icon="pi pi-times" onClick={handleCancel} className="p-button-danger" raised />
                </div>
            </form>
        </div>
    );
}
