import React, { useState } from 'react';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import './AddAuthor.css'; 

export function AddAuthor(props) {
    const [authorData, setAuthorData] = useState({
        authorFirstName: '',
        authorLastName: '',
        nationality: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAuthorData({
            ...authorData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch("http://localhost:8080/author", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(authorData)
        })
        .then(response => response.json())
        .then(data => {
            console.log("Autor agregado exitosamente:", data);
            props.onSave();
        })
        .catch(error => {
            console.error("Error al agregar autor:", error);
        });
    };

    const handleCancel = () => {
        props.onCancel();
    };

    return (
        <div>
            <form className="add-author-form" onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Nombre:</label>
                    <InputText
                        name="authorFirstName"
                        value={authorData.authorFirstName}
                        onChange={handleChange}
                        className="p-inputtext-sm" // Clase para el tamaño pequeño del input
                    />
                </div>
                <div className="form-group">
                    <label>Apellido:</label>
                    <InputText
                        name="authorLastName"
                        value={authorData.authorLastName}
                        onChange={handleChange}
                        className="p-inputtext-sm" // Clase para el tamaño pequeño del input
                    />
                </div>
                <div className="form-group">
                    <label>Nacionalidad:</label>
                    <InputText
                        name="nationality"
                        value={authorData.nationality}
                        onChange={handleChange}
                        className="p-inputtext-sm" // Clase para el tamaño pequeño del input
                    />
                </div>
                <div className="button-group">
                    <Button type="submit" label="Guardar" className="p-button-sm p-button-primary" />
                    <Button type="button" label="Cancelar" onClick={handleCancel} className="p-button-sm p-button-secondary" />
                </div>
            </form>
        </div>
    );
}
