import React, { useState } from 'react';
import { Dialog } from 'primereact/dialog';
import { Button } from 'primereact/button';

function AuthorForm({ visible, onSave, onCancel }) {
    const [author, setAuthor] = useState({ authorFirstName: '', authorLastName: '', nationality: '' });

    const saveAuthor = () => {
        onSave(author);
        setAuthor({ authorFirstName: '', authorLastName: '', nationality: '' });
    };

    return (
        <Dialog header="Nuevo Autor" visible={visible} onHide={onCancel}>
            <div>
                <div>
                    <span>Nombre: </span>
                    <input type="text" value={author.authorFirstName} onChange={(e) => setAuthor({ ...author, authorFirstName: e.target.value })} />
                </div>
                <div>
                    <span>Apellido: </span>
                    <input type="text" value={author.authorLastName} onChange={(e) => setAuthor({ ...author, authorLastName: e.target.value })} />
                </div>
                <div>
                    <span>Nacionalidad: </span>
                    <input type="text" value={author.nationality} onChange={(e) => setAuthor({ ...author, nationality: e.target.value })} />
                </div>
            </div>
            <Button label="Guardar" icon="pi pi-check" className="p-button-success" onClick={saveAuthor} />
            <Button label="Cancelar" icon="pi pi-times" className="p-button-danger" onClick={onCancel} />
        </Dialog>
    );
}

export default AuthorForm;
