import React, { useState, useEffect } from 'react';
import { Button } from 'primereact/button';

export function Student(props) {
    const [students, setStudents] = useState([]);

    useEffect(() => {
        // Lógica para obtener la lista de estudiantes desde el servidor
        fetch('http://localhost:8080/students')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Error al obtener la lista de estudiantes');
                }
                return response.json();
            })
            .then(data => {
                if (Array.isArray(data)) {
                    setStudents(data);
                } else {
                    throw new Error('Los datos recibidos no son un array');
                }
            })
            .catch(error => console.error(error));
    }, []);
    

    const handleAddStudent = () => {
        props.history.push('/add-student');
    };

    const handleEditStudent = (studentId) => {
        props.history.push(`/edit-student/${studentId}`);
    };

    return (
        <div className="student-container">
            <h2>Lista de Estudiantes</h2>
            <Button label="Agregar Estudiante" onClick={handleAddStudent} className="p-button-secondary" />
            <ul>
                {students.map(student => (
                    <li key={student.id}>
                        <div>{`${student.firstName} ${student.lastName}`}</div>
                        <div>{`ID: ${student.studentId}`}</div>
                        <Button label="Editar" onClick={() => handleEditStudent(student.id)} className="p-button-secondary" />
                    </li>
                ))}
            </ul>
        </div>
    );
}
