// Book.js
import React, { useState, useEffect } from 'react';
import { Button } from 'primereact/button';
import '../../App.css'; // Importa las clases de estilo desde App.css

export function Book(props) {
    const [books, setBooks] = useState([]);

    useEffect(() => {
        // Lógica para obtener la lista de libros desde el servidor
        fetchData();
    }, []);

    const fetchData = () => {
        fetch('http://localhost:8080/books')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Error al obtener la lista de libros');
                }
                return response.json();
            })
            .then(data => {
                setBooks(data);
            })
            .catch(error => console.error(error));
    };

    const handleAddBook = () => {
        props.history.push('/add-book');
    };

    const handleEditBook = (bookId) => {
        props.history.push(`/edit-book/${bookId}`);
    };

    const handleDeleteBook = (bookId) => {
        fetch(`http://localhost:8080/books/${bookId}`, {
            method: 'DELETE'
        })
            .then(() => {
                console.log("Libro eliminado exitosamente");
                fetchData(); // Actualiza la lista después de eliminar
            })
            .catch(error => {
                console.error("Error al eliminar libro:", error);
            });
    };

    return (
        <div className="book-container">
            <h2>Lista de Libros</h2>
            <Button label="Agregar Libro" onClick={handleAddBook} className="p-button-secondary" />
            <ul className="book-list">
                {books.map(book => (
                    <li key={book.id} className="book-item">
                        <div>{`${book.title} - ${book.author}`}</div>
                        <div>{`ISBN: ${book.isbn}`}</div>
                        <Button label="Editar" onClick={() => handleEditBook(book.id)} className="p-button-secondary" />
                        <Button label="Eliminar" onClick={() => handleDeleteBook(book.id)} className="p-button-danger" /> {/* Botón para eliminar libro */}
                    </li>
                ))}
            </ul>
        </div>
    );
}
