import React, { useEffect, useState } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import { Dialog } from 'primereact/dialog';
import { AddAuthor } from './AddAuthor';
import { EditAuthor } from './EditAuthor';

export function Author() {
    const [authors, setAuthors] = useState([]);
    const [displayAddDialog, setDisplayAddDialog] = useState(false);
    const [displayEditDialog, setDisplayEditDialog] = useState(false);
    const [selectedAuthor, setSelectedAuthor] = useState(null);
    const [searchId, setSearchId] = useState(''); // Estado para el ID de búsqueda
    const [searchedAuthor, setSearchedAuthor] = useState(null); // Estado para almacenar el autor buscado
    const [displaySearchModal, setDisplaySearchModal] = useState(false); // Estado para controlar la visibilidad del modal de búsqueda

    useEffect(() => {
        document.title = 'Autores';
        if (searchId !== '') { // Realizar búsqueda solo si el ID de búsqueda no está vacío
            fetchData();
        }
    }, [searchId]); // Ejecutar fetchData cuando cambia el ID de búsqueda

    const fetchData = () => {
        // Realizar la solicitud para obtener el autor específico
        fetch(`http://localhost:8080/author/${searchId}`, {
            method: "GET",
        })
            .then((res) => res.json())
            .then((data) => {
                setSearchedAuthor(data); // Guarda el autor buscado en el estado
            })
            .catch(error => {
                console.error("Error al obtener autor:", error);
            });
    };

    const searchAuthor = () => {
        if (searchId !== '') { // Verificar si el ID de búsqueda no está vacío
            setDisplaySearchModal(true); // Abrir el modal de búsqueda
            fetchData(); // Realizar búsqueda solo si el ID de búsqueda no está vacío
        }
    };

    const handleCancel = () => {
        setDisplaySearchModal(false); // Cerrar el modal de búsqueda
        setSearchId(''); // Limpiar el campo de búsqueda
        setSearchedAuthor(null); // Limpiar el autor buscado
    };

    return (
        <div>
            <h1 style={{ textAlign: 'center' }}>Lista de Autores</h1>
            <div style={{ textAlign: 'left', marginBottom: '20px', marginLeft: '50px' }}>
                <Button label="Nuevo Autor" icon="pi pi-user-plus" onClick={() => setDisplayAddDialog(true)} className="p-button-success" text raised rounded />
            </div>

            {/* Agregar campo de entrada para ingresar el ID de búsqueda */}
            <div style={{ textAlign: 'center', marginLeft: '50px', marginRight: '50px' }}>
                <input type="text" placeholder="ID de Autor" value={searchId} onChange={(e) => setSearchId(e.target.value)} />
                <Button label="Buscar" onClick={searchAuthor} />
            </div>

            {searchedAuthor ? ( // Si se encontró un autor con el ID de búsqueda
                <div style={{ textAlign: 'center', marginLeft: '50px', marginRight: '50px' }}>
                    {/* Mostrar los detalles del autor buscado */}
                    <p>Detalles del Autor:</p>
                    <p>Nombre: {searchedAuthor.authorFirstName}</p>
                    <p>Apellido: {searchedAuthor.authorLastName}</p>
                    <p>Nacionalidad: {searchedAuthor.nationality}</p>
                </div>
            ) : (
                <div style={{ textAlign: 'center', marginLeft: '50px', marginRight: '50px' }}>
                    <p>No se encontró ningún autor con el ID proporcionado.</p>
                </div>
            )}

            {/* Modal de búsqueda */}
            <Dialog header="Buscar Autor" visible={displaySearchModal} style={{ width: '45vw' }} onHide={handleCancel}>
                {/* Agregar el contenido del modal de búsqueda aquí */}
            </Dialog>

            {/* Resto del código... */}
        </div>
    );

}
