import React, { useState, useEffect } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import { Dialog } from 'primereact/dialog';
import { AddAuthor } from './AddAuthor';
import { EditAuthor } from './EditAuthor';

export function Author() {
    const [authors, setAuthors] = useState([]);
    const [displayAddDialog, setDisplayAddDialog] = useState(false);
    const [displayEditDialog, setDisplayEditDialog] = useState(false);
    const [selectedAuthor, setSelectedAuthor] = useState(null);
    const [searchId, setSearchId] = useState('');

    useEffect(() => {
        document.title = 'Autores';
        fetchData();
    }, []);

    const fetchData = () => {
        fetch("http://localhost:8080/author", {
            method: "GET",
        })
            .then((res) => res.json())
            .then((data) => {
                setAuthors(data); // Guarda los autores en el estado
            });
    };

    const saveAuthor = () => {
        setDisplayAddDialog(false);
        fetchData(); // Actualiza la lista después de guardar
    };

    const editAuthor = (author) => {
        setSelectedAuthor(author);
        setDisplayEditDialog(true);
    };

    const deleteAuthor = (author) => {
        fetch(`http://localhost:8080/author/${author.id}`, {
            method: "DELETE",
        })
            .then(() => {
                console.log("Autor eliminado exitosamente");
                fetchData(); // Actualiza la lista después de eliminar
            })
            .catch(error => {
                console.error("Error al eliminar autor:", error);
            });
    };

    const handleSearch = () => {
        console.log('Buscar autor por ID:', searchId);
        // Aquí puedes implementar la lógica para buscar por ID
    };

    return (
        <div>
            <h1 style={{ textAlign: 'center' }}>Lista de Autores</h1>
            <div style={{ textAlign: 'left', marginBottom: '20px', marginLeft: '50px' }}>
                <Button label="Buscar por ID" icon="pi pi-search" onClick={handleSearch} className="p-button-secondary" text raised rounded />
                <Button label="Nuevo Autor" icon="pi pi-user-plus" onClick={() => setDisplayAddDialog(true)} className="p-button-success" text raised rounded />
            </div>

            <div style={{ textAlign: 'center', marginLeft: '50px', marginRight: '50px' }}>
                <div style={{ maxWidth: '100%', overflowX: 'auto' }}>
                    <DataTable value={authors} stripedRows style={{ width: '100%' }}>
                        <Column field="authorFirstName" header="Nombre" />
                        <Column field="authorLastName" header="Apellido" />
                        <Column field="nationality" header="Nacionalidad" />
                        <Column body={(rowData) => (
                            <div style={{ display: 'flex', gap: '10px', textAlign: 'center', marginBottom: '20px' }}>
                                <Button label='Editar' icon="pi pi-pencil" onClick={() => editAuthor(rowData)} className="p-button-secondary" text raised rounded />
                                <Button label='Eliminar' icon="pi pi-trash" onClick={() => deleteAuthor(rowData)} className="p-button-danger" text raised rounded />
                            </div>
                        )} />
                    </DataTable>
                </div>
            </div>

            <Dialog header="Agregar Nuevo Autor" headerClassName="custom-dialog-header" visible={displayAddDialog} style={{ width: '45vw' }} onHide={() => setDisplayAddDialog(false)}>
                <AddAuthor onSave={saveAuthor} onCancel={() => setDisplayAddDialog(false)} />
            </Dialog>
            <Dialog header="Editar Autor" headerClassName="custom-dialog-header" visible={displayEditDialog} style={{ width: '45vw' }} onHide={() => setDisplayEditDialog(false)}>
                {selectedAuthor && <EditAuthor author={selectedAuthor} onSave={() => { setDisplayEditDialog(false); fetchData(); }} onCancel={() => setDisplayEditDialog(false)} />}
            </Dialog>
        </div>
    );
}
