export function AddAuthor(props) {
    const [authorData, setAuthorData] = useState({
        authorFirstName: '', // Cambiado de firstName a authorFirstName
        authorLastName: '', // Cambiado de lastName a authorLastName
        nationality: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAuthorData({
            ...authorData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch("http://localhost:8080/author", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(authorData)
        })
        .then(response => response.json())
        .then(data => {
            console.log("Autor agregado exitosamente:", data);
            props.onSave(); // Llama a la funci贸n de actualizaci贸n
        })
        .catch(error => {
            console.error("Error al agregar autor:", error);
        });
    };

    const handleCancel = () => {
        console.log('Cancelar');
        props.onCancel();
    };

    return (
        <div className="add-author-form">
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Nombre:</label>
                    <input
                        type="text"
                        name="authorFirstName"
                        value={authorData.authorFirstName}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Apellido:</label>
                    <input
                        type="text"
                        name="authorLastName"
                        value={authorData.authorLastName}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Nacionalidad:</label>
                    <input
                        type="text"
                        name="nationality"
                        value={authorData.nationality}
                        onChange={handleChange}
                    />
                </div>
                <div className="button-group">
                    <Button label="Guardar" icon="pi pi-save" type="submit" className="p-button-secondary" raised />
                    <Button label="Cancelar" icon="pi pi-times" onClick={handleCancel} className="p-button-danger" raised />
                </div>
            </form>
        </div>
    );
}
