import React, { useEffect, useState } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import 'primereact/resources/themes/saga-orange/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';
import { Dialog } from 'primereact/dialog';
import { AddAuthor } from './AddAuthor';
import { EditAuthor } from './EditAuthor';

export function Author() {
    const [authors, setAuthors] = useState([]);
    const [displayAddDialog, setDisplayAddDialog] = useState(false);
    const [displayEditDialog, setDisplayEditDialog] = useState(false);
    const [selectedAuthor, setSelectedAuthor] = useState(null);
    const [searchId, setSearchId] = useState(null); // Nuevo estado para el ID de búsqueda
    const [searchedAuthor, setSearchedAuthor] = useState(null); // Nuevo estado para almacenar el autor buscado

    useEffect(() => {
        document.title = 'Autores';
        fetchData();
    }, [searchId]); // Ejecutar fetchData nuevamente cuando cambia el ID de búsqueda

    const fetchData = () => {
        if (searchId) {
            // Si hay un ID de búsqueda, realizar la solicitud para obtener el autor específico
            fetch(`http://localhost:8080/author/${searchId}`, {
                method: "GET",
            })
                .then((res) => res.json())
                .then((data) => {
                    setSearchedAuthor(data); // Guarda el autor buscado en el estado
                })
                .catch(error => {
                    console.error("Error al obtener autor:", error);
                });
        } else {
            // Si no hay un ID de búsqueda, obtener todos los autores
            fetch("http://localhost:8080/author", {
                method: "GET",
            })
                .then((res) => res.json())
                .then((data) => {
                    setAuthors(data); // Guarda los autores en el estado
                })
                .catch(error => {
                    console.error("Error al obtener autores:", error);
                });
        }
    };

    const searchAuthor = (id) => {
        setSearchId(id); // Establecer el ID de búsqueda
    };

    const saveAuthor = () => {
        setDisplayAddDialog(false);
        fetchData(); // Actualiza la lista después de guardar
    };

    const editAuthor = (author) => {
        setSelectedAuthor(author);
        setDisplayEditDialog(true);
    };

    const deleteAuthor = (author) => {
        fetch(`http://localhost:8080/author/${author.id}`, {
            method: "DELETE",
        })
            .then(() => {
                console.log("Autor eliminado exitosamente");
                fetchData(); // Actualiza la lista después de eliminar
            })
            .catch(error => {
                console.error("Error al eliminar autor:", error);
            });
    };

    const handleEditSave = () => {
        setDisplayEditDialog(false);
        fetchData(); // Actualiza la lista después de guardar la edición
    };

    const handleEditCancel = () => {
        setDisplayEditDialog(false);
    };

    return (
        <div>
            <h1 style={{ textAlign: 'center' }}>Lista de Autores</h1>
            <div style={{ textAlign: 'left', marginBottom: '20px', marginLeft: '50px' }}>
                <Button label="Nuevo Autor" icon="pi pi-user-plus" onClick={() => setDisplayAddDialog(true)} className="p-button-success" text raised rounded />
            </div>

            {/* Agregar campo de entrada para ingresar el ID de búsqueda */}
            <div style={{ textAlign: 'center', marginLeft: '50px', marginRight: '50px' }}>
                <input type="text" placeholder="ID de Autor" onChange={(e) => setSearchId(e.target.value)} />
                <Button label="Buscar" onClick={() => searchAuthor(searchId)} />
            </div>

            {searchedAuthor ? ( // Si se encontró un autor con el ID de búsqueda
                <div style={{ textAlign: 'center', marginLeft: '50px', marginRight: '50px' }}>
                    {/* Mostrar los detalles del autor buscado */}
                    <p>Detalles del Autor:</p>
                    <p>Nombre: {searchedAuthor.authorFirstName}</p>
                    <p>Apellido: {searchedAuthor.authorLastName}</p>
                    <p>Nacionalidad: {searchedAuthor.nationality}</p>
                </div>
            ) : (
                <div style={{ textAlign: 'center', marginLeft: '50px', marginRight: '50px' }}>
                    <div style={{ maxWidth: '100%', overflowX: 'auto' }}>
                        {/* Mostrar la tabla de autores si no se ha realizado una búsqueda */}
                        <DataTable value={authors} stripedRows style={{ width: '100%' }}>
                            <Column field="authorFirstName" header="Nombre" />
                            <Column field="authorLastName" header="Apellido" />
                            <Column field="nationality" header="Nacionalidad" />
                            <Column body={(rowData) => (
                                <div style={{ display: 'flex', gap: '10px', textAlign: 'center', marginBottom: '20px' }}>
                                    <Button label='Editar' icon="pi pi-pencil" onClick={() => editAuthor(rowData)} className="p-button-secondary" text raised rounded />
                                    <Button label='Eliminar' icon="pi pi-trash" onClick={() => deleteAuthor(rowData)} className="p-button-danger" text raised rounded />
                                </div>
                            )} />
                        </DataTable>
                    </div>
                </div>
            )}

            <Dialog header="Agregar Nuevo Autor" headerClassName="custom-dialog-header" visible={displayAddDialog} style={{ width: '45vw' }} onHide={() => setDisplayAddDialog(false)}>
                <AddAuthor onSave={saveAuthor} onCancel={() => setDisplayAddDialog(false)} />
            </Dialog>
            <Dialog header="Editar Autor" headerClassName="custom-dialog-header" visible={displayEditDialog} style={{ width: '45vw' }} onHide={handleEditCancel}>
                {selectedAuthor && <EditAuthor author={selectedAuthor} onSave={handleEditSave} onCancel={handleEditCancel} />}
            </Dialog>
        </div>
    );

}
