import React, { useState, useEffect } from 'react';
import { Button } from 'primereact/button';

export function EditBook(props) {
    const [bookData, setBookData] = useState({
        title: '',
        author: '',
        genre: '',
        year: ''
    });

    useEffect(() => {
        if (props.book) {
            setBookData({
                title: props.book.title,
                author: props.book.author,
                genre: props.book.genre,
                year: props.book.year
            });
        }
    }, [props.book]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setBookData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch(`http://localhost:8080/book/${props.book.id}`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(bookData)
        })
            .then(response => {
                if (response.ok) {
                    console.log("Libro actualizado exitosamente");
                    props.onSave();
                } else {
                    throw new Error('Error al actualizar libro');
                }
            })
            .catch(error => {
                console.error("Error al actualizar libro:", error);
            });
    };

    const handleCancel = () => {
        props.onCancel();
    };

    return (
        <div className="edit-book-form">
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Título:</label>
                    <input
                        type="text"
                        name="title"
                        value={bookData.title}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Autor:</label>
                    <input
                        type="text"
                        name="author"
                        value={bookData.author}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Género:</label>
                    <input
                        type="text"
                        name="genre"
                        value={bookData.genre}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Año:</label>
                    <input
                        type="text"
                        name="year"
                        value={bookData.year}
                        onChange={handleChange}
                    />
                </div>
                <div className="button-group" style={{ textAlign: 'center', marginBottom: '20px' }}>
                    <Button label="Guardar" icon="pi pi-save" type="submit" className="p-button-secondary" raised rounded />
                    <Button label="Cancelar" icon="pi pi-times" onClick={handleCancel} className="p-button-danger" raised rounded />
                </div>
            </form>
        </div>
    );
}
