import React, { useEffect, useState } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import 'primereact/resources/themes/saga-orange/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';
import { Dialog } from 'primereact/dialog';
import AddAuthor from './AddAuthor';



export function Author() {
    const [authors, setAuthors] = useState([]);
    const [displayDialog, setDisplayDialog] = useState(false);
    const [author, setAuthor] = useState(null);

    useEffect(() => {
        fetchData();
    }, []); 

    const fetchData = () => {
        fetch("http://localhost:8080/author", {
            method: "GET",
        })
        .then((res) => res.json())
        .then((data) => {
            setAuthors(data); // Guarda los autores en el estado
        });
    };

    const saveAuthor = () => {
        // Lógica para guardar el autor en el servidor
        setDisplayDialog(false);
        fetchData(); // Actualiza la lista después de guardar
    };

    const editAuthor = (author) => {
        // Lógica para editar un autor
        setAuthor(author);
        setDisplayDialog(true);
    };

    const deleteAuthor = (author) => {
        // Lógica para eliminar un autor
        fetchData(); // Actualiza la lista después de eliminar
    };


    return (
        <div>
            <h1 style={{textAlign: 'center'}}>Lista de Autores</h1>
            <Button label="Nuevo Autor" icon="pi pi-user-plus" onClick={() => setDisplayDialog(true)} className="p-button-success" />

            <DataTable value={authors}>
                <Column field="authorFirstName" header="Nombre" />
                <Column field="authorLastName" header="Apellido" />
                <Column field="nationality" header="Nacionalidad" />
                <Column body={(rowData) => (
                    <div>
                        <Button icon="pi pi-pencil" onClick={() => editAuthor(rowData)} className="p-button-secondary" />
                        <Button icon="pi pi-trash" onClick={() => deleteAuthor(rowData)} className="p-button-danger" />
                    </div>
                )} />
            </DataTable>

            <Dialog
    visible={displayDialog}
    onHide={() => setDisplayDialog(false)}
    modal
    style={{ width: '50vw', borderRadius: '12px' }}
    className="p-dialog-custom"
>
    <div className="p-d-flex p-flex-column p-fluid">
        <div className="p-dialog-header">
            <h5>Agregar Nuevo Autor</h5>
        </div>
        <div className="p-dialog-content">
            {/* Contenido del formulario aquí */}
        </div>
        <div className="p-dialog-footer">
            <Button label="Guardar" className="p-button-success" onClick={handleSubmit} />
            <Button label="Cancelar" className="p-button-secondary" onClick={handleCancel} />
        </div>
    </div>
</Dialog>
        </div>
    );
}
