import { useEffect, useState } from 'react';
import '../App.css';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';

import { Checkbox } from "primereact/checkbox";
import { Button } from 'primereact/button';

import { SideBarAdmin } from './SideBarAdmin';

export function TicketTable(){

    const [tickets,setTicket]=useState()
    const [first, setFirst] = useState(0); // Primer índice de la página actual
    const rowsPerPage = 15; 
    const ticketSize=tickets.size()

    useEffect(() => {
        fetchTickets();
    }, []); // Solo se ejecuta una vez al montar el componente



   const fetchTickets=(()=>{
    fetch("http://localhost:8080/ticket",{
        method:"GET"
    })
    .then((res=>res.json()))
    .then((data)=>{

        const {content}=data


        setTicket(content)





    })
   }) 

   const handlePageChange = (event) => {
    setFirst(event.first);
};


    const handleButtonClick = (idUser,idTicket)=>{

        const data ={idUser:idUser}

        const newState ={ticketState: true,id:idTicket}
        console.log(newState)


        fetch('http://localhost:8080/receipt', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json' // Asegúrate de establecer el tipo de contenido adecuado
            },
            body: JSON.stringify(data) // Convierte tu objeto de datos a formato JSON
        })
        .then(response => response.json())
        .then(dataReceipt => {
            console.log('Respuesta del servidor:', dataReceipt);
            fetch(`http://localhost:8080/ticket/${newState.id}`,{
                method:'PUT',
                headers:{
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(newState)

            })
            .then(response => response.json())
            .then(updateTicket=>{
                console.log("EL ticket se acutalizo")
                console.log(updateTicket);

            })
            .catch(error=>{
                console.error("ERROR AL ENVIAR")

            })

        })
        .catch(error => {
            console.error('Error al enviar la solicitud:', error);
        });
    }



    return(
        <>
        <div className="card">
            <SideBarAdmin/>
            <DataTable value={tickets} 
            paginator 
            rows={rowsPerPage} 
            totalRecords={tickets.length} 
            tableStyle={{ minWidth: '50rem' }}
            className='card-table'>
            <Column field="id" header="Ticket Id" />
            <Column field="user.firstName" header="First Name"/>
            <Column field="user.lastName" header=" Last Name"/>
            <Column field="user.department" header="Departament"/>
            <Column field="description" header="Description"/>
            <Column body={(ticket) => 
                <div>
                    <Checkbox checked={ticket.ticketState} />
                    {!ticket.ticketState && <Button onClick={() => handleButtonClick(ticket.user.id,ticket.id )} label="Receipt" className='button' />}

                </div>}
                header="Ticket State"/>

            </DataTable>
        </div>



        </>)

    }
