import React, { useState } from 'react';
import { Dialog } from 'primereact/dialog';
import { Button } from 'primereact/button';

export function AuthorForm({ visible, onSave, onCancel }) {
    const [author, setAuthor] = useState({ firstName: '', lastName: '', nationality: '' });

    const saveAuthor = () => {
        onSave(author);
        setAuthor({ firstName: '', lastName: '', nationality: '' });
    };

    return (
        <Dialog header="Nuevo Autor" visible={visible} onHide={onCancel}>
            <form onSubmit={saveAuthor}>
                <div className="p-field">
                    <label htmlFor="firstName">Nombre</label>
                    <input type="text" id="firstName" value={author.firstName} onChange={(e) => setAuthor({ ...author, firstName: e.target.value })} />
                </div>
                <div className="p-field">
                    <label htmlFor="lastName">Apellido</label>
                    <input type="text" id="lastName" value={author.lastName} onChange={(e) => setAuthor({ ...author, lastName: e.target.value })} />
                </div>
                <div className="p-field">
                    <label htmlFor="nationality">Nacionalidad</label>
                    <input type="text" id="nationality" value={author.nationality} onChange={(e) => setAuthor({ ...author, nationality: e.target.value })} />
                </div>
                <Button type="submit" label="Guardar" />
                <Button type="button" label="Cancelar" onClick={onCancel} className="p-button-secondary" />
            </form>
        </Dialog>
    );
}
