// EditAuthor.js
import React, { useState, useEffect } from 'react';
import { Button } from 'primereact/button';
import axios from 'axios';

export function EditAuthor(props) {
    const [authorData, setAuthorData] = useState({
        authorFirstName: '',
        authorLastName: '',
        nationality: ''
    });

    useEffect(() => {
        setAuthorData(props.author);
        fetchAuthorData();
    }, [props.author]);

    const fetchAuthorData = async () => {
        try {
            const response = await axios.get('http://localhost:8080/authors/' + props.author.id);
            if (response) {
                setAuthorData(response.data);
            }
        } catch (error) {
            console.error("Error fetching author data:", error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAuthorData({
            ...authorData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.put('http://localhost:8080/authors/' + props.author.id, authorData);
            if (response) {
                props.onUpdate(authorData);
            }
        } catch (error) {
            console.error("Error updating author:", error);
        }
    };

    const handleCancel = () => {
        props.onCancel();
    };

    return (
        <div className="add-author-form">
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Nombre:</label>
                    <input
                        type="text"
                        name="authorFirstName"
                        value={authorData.authorFirstName}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Apellido:</label>
                    <input
                        type="text"
                        name="authorLastName"
                        value={authorData.authorLastName}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Nacionalidad:</label>
                    <input
                        type="text"
                        name="nationality"
                        value={authorData.nationality}
                        onChange={handleChange}
                    />
                </div>
                <div className="button-group">
                    <Button label="Guardar" icon="pi pi-save" type="submit" className="p-button-secondary" raised />
                    <Button label="Cancelar" icon="pi pi-times" onClick={handleCancel} className="p-button-danger" raised />
                </div>
            </form>
        </div>
    );
}
