import { useState,useEffect,useRef } from 'react';
import './App.css';
import { Dropdown } from 'primereact/dropdown';
import { Toast } from 'primereact/toast';
import { InputTextarea } from 'primereact/inputtextarea';
import { Button } from 'primereact/button';


function App() {
  const toast = useRef(null);

  const [userOptions, setUserOptions] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [value, setValue] = useState('');


  const showSuccess = () => {
    toast.current.show({severity:'success', summary: 'Success', detail:'Message Content', life: 3000});
}

  const handleClick = () => {
    // Verifica que se haya seleccionado un usuario
    if (!selectedUser) {
      alert("Please select a user");
      return;
    }

    // Crea el objeto de datos a enviar en la solicitud POST
    const data = {
      description: value,
      idUser: selectedUser.code
    };

    // Realiza la solicitud POST
    fetch("http://localhost:8080/ticket", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(data)
    })
    .then(response => {
      if (!response.ok) {
        throw new Error("Failed to send data");
      }
      return response.json();
    })
    .then(data => {
      // Maneja la respuesta si es necesario
      console.log("Data sent successfully:", data);
      // Reinicia los valores
      setSelectedUser(null);
      setValue('');
    })
    .catch(error => {
      // Maneja el error si ocurre
      console.error("Error sending data:", error);
    });
    showSuccess();
  };


  useEffect(() => {
    fetch("http://localhost:8080/user", {
      method: "GET",
    })
      .then((res) => res.json())
      .then((data) => {
        const firstNames=data.map(user=>({name:user.firstName+'   '+user.lastName, code: user.id}))
        setUserOptions(firstNames);

      });

  }, []);
  useEffect(()=>{

    console.log(value)
    console.log(selectedUser)
  },[value])


  return (
    <>


    <h1 className="title">
    CNPC Chuanqing Drilling Engineering Company Limited (CCDC)
    <img src="https://www.yswinch.com/wp-content/uploads/2017/11/cnpc-logo_d.jpg" className="imageLogo" />
    </h1>

    <div className='container'>
      <div className='form-container'>
      <h3>Report your problem</h3>
        <div className='form'>
          <h4 className='form-h4'>Please choose your name</h4>
          <Dropdown
            value={selectedUser}
            onChange={(e) => setSelectedUser(e.value)}
            options={userOptions}
            optionLabel="name"
            placeholder="Select a User"
            className="w-full md:w-30rem"
          />
          <div className="description-container">
            <h4 className='form-h4'>Description</h4>
            <InputTextarea value={value} onChange={(e) => setValue(e.target.value)} rows={5} cols={30} />
          </div>
          <Toast ref={toast} />
          <Button label="Send" type="submit" icon="pi pi-check" className="mt-4 custom-button" onClick={handleClick} />
        </div>
      </div>
    </div>
    </>
  );
}

export default App;
