import { useEffect, useState } from 'react';
import '../App.css';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';

import { Checkbox } from "primereact/checkbox";
import { Button } from 'primereact/button';

export function ReceiptTable(){

    const [ticket,setTicket]=useState()



    useEffect(()=>{
    fetch("http://localhost:8080/ticket",{
        method:"GET"
    })
    .then((res=>res.json()))
    .then((data)=>{

        const {content}=data


        setTicket(content)

    })

    const handleButtonClick = (ticketId) => {
        // Handle button click for the specific ticket
        console.log(`Button clicked for ticket ID: ${ticketId}`);
    };


    },[])
    console.log(ticket)
    return(
        <>
        <div className="card">
            <DataTable value={ticket} paginator rows={5} rowsPerPageOptions={[5, 10, 25, 50]} tableStyle={{ minWidth: '50rem' }}>
            <Column field="id" header="Code"/>
                <Column field="user.firstName" header="First Name"/>
                <Column field="user.lastName" header=" Name"/>
                <Column field="user.department" header="Departament"/>
                <Column body={(ticket) => 
                <div>
                    <Checkbox checked={ticket.ticketState} />
                    {!ticket.ticketState && <Button onClick={() => handleButtonClick(ticket.id)} label="Action" className='button' />}

                </div>}
                header="Ticket State"/>

            </DataTable>
        </div>



        </>)

    }
