import React, { useState, useEffect } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import { Dialog } from 'primereact/dialog';
import { AddAuthor } from './AddAuthor';
import { EditAuthor } from './EditAuthor';

export function Author() {
    const [authors, setAuthors] = useState([]);
    const [displayAddDialog, setDisplayAddDialog] = useState(false);
    const [displayEditDialog, setDisplayEditDialog] = useState(false);
    const [selectedAuthor, setSelectedAuthor] = useState(null);
    const [searchId, setSearchId] = useState('');

    useEffect(() => {
        document.title = 'Autores';
        fetchData();
    }, []);

    const fetchData = () => {
        fetch("http://localhost:8080/author", {
            method: "GET",
        })
            .then((res) => res.json())
            .then((data) => {
                setAuthors(data);
            });
    };

    const saveAuthor = () => {
        setDisplayAddDialog(false);
        fetchData();
    };

    const editAuthor = (author) => {
        setSelectedAuthor(author);
        setDisplayEditDialog(true);
    };

    const deleteAuthor = (author) => {
        fetch(`http://localhost:8080/author/${author.id}`, {
            method: "DELETE",
        })
            .then(() => {
                console.log("Autor eliminado exitosamente");
                fetchData();
            })
            .catch(error => {
                console.error("Error al eliminar autor:", error);
            });
    };

    const handleSearch = () => {
        console.log('Buscar autor por ID:', searchId);
        // Aquí puedes implementar la lógica para buscar por ID
    };

    return (
        <div>
            <h1 style={{ textAlign: 'center' }}>Lista de Autores</h1>
            <div style={{ textAlign: 'left', marginBottom: '20px', marginLeft: '50px' }}>
                <Button label="Buscar por ID" icon="pi pi-search" onClick={handleSearch} className="p-button-secondary" text raised rounded />
                <Button label="Nuevo Autor" icon="pi pi-user-plus" onClick={() => setDisplayAddDialog(true)} className="p-button-success" text raised rounded />
            </div>

            {/* Resto del código para mostrar la lista de autores y diálogos de agregar/editar */}
        </div>
    );
}
